def DF2Mat(dd,idx_lst,vname):
  import sys
  import numpy as np
  from pandas import DataFrame
  ret_lst, num_lst=[],[]
  for c in idx_lst:
    lst=eval('list(set(dd.'+c+'))');lst.sort()
