  df=df.loc[boo].reset_index(drop=True)
  
  Latitude_Pole, Longitude_Pole = 23.61000, 120.9900
  pnyc = Proj(proj='lcc', datum='NAD83', lat_1=10, lat_2=40,lat_0=Latitude_Pole, lon_0=Longitude_Pole, x_0=0, y_0=0.0)
